import React from 'react';

export default class Pokecard extends React.Component{
    state = {
        name: '',
        imageUrl: '',
        pokemonIndex: ''
    };

    async componentDidMount(){
        const { name, url } = this.props;
        const urlSplit = url.split('/');
        let splitName = name.split(" -");
        for (let i = 0; i < splitName.length; i++ ){
            splitName[i] = splitName[i][0].toUpperCase() + splitName[i].substring(1);
        }
        let capName = splitName.join(" "); 
        const pokemonIndex = urlSplit[urlSplit.length - 2]
        const imageUrl = "https://pokeres.bastionbot.org/images/pokemon/" + pokemonIndex + ".png"
        this.setState({name: capName, imageUrl: imageUrl, pokemonIndex: pokemonIndex})
    };

    render(){
        return (
            <div className='col-lg-3 col-md-4 col-sm-6 md-5'>
                <h1>{this.state.name}</h1>
                <img src={this.state.imageUrl} alt= {this.state.name} width="100em" height="100em"></img>
            </div>
        );
    }
}
