import React, {useState, useEffect} from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css'


class Pokedex extends React.Component{
    render(){
        return (
            <div>
                <h1>{this.props.name}</h1>
                <div className='container'>
                    <Pokelist />
                </div>
            </div>
        );
    }
}

class Pokecard extends React.Component{
    state = {
        name: '',
        imageUrl: '',
        pokemonIndex: ''
    };

    async componentDidMount(){
        const { name, url } = this.props;
        const urlSplit = url.split('/');
        let splitName = name.split(' -');
        splitName.map((substr) =>{
            let newName = substr[0].toUpperCase() + substr.substring(1);
            return newName;
        })
        let capName = splitName.join(""); 
        console.log(capName);
        const pokemonIndex = urlSplit[urlSplit.length - 2]
        const imageUrl = "https://pokeres.bastionbot.org/images/pokemon/" + pokemonIndex + ".png"
        this.setState({name: capName, imageUrl: imageUrl, pokemonIndex: pokemonIndex})
    };

    render(){
        return (
            <div className='col-lg-3 col-md-4 col-sm-6 md-5'>
                <h1>{this.state.name}</h1>
                <img src={this.state.imageUrl} alt= {this.state.name} width="100em" height="100em"></img>
            </div>
        );
    }
}

class Pokelist extends React.Component{
    state = {
        url: "https://pokeapi.co/api/v2/pokemon/",
        pokemon: null,
        prev: null,
        next: null
    };

    async updatePokelist(newUrl){
        const res = await axios.get(newUrl);
        this.setState({pokemon: res.data["results"], prev: res.data["previous"], next: res.data["next"]});
        console.log(res.data["previous"]);
        console.log(res.data["next"]);
    }

    prevPage = () => {
        this.updatePokelist(this.state.prev);
    }

    nextPage = () => {
        this.updatePokelist(this.state.next);
    }

    async componentDidMount(){
        const res = await axios.get(this.state.url);
        this.setState({pokemon: res.data["results"], prev: res.data["previous"], next: res.data["next"]});
    }

    render(){
        return (
            (this.state.pokemon ? 
            <div className='row'>
                    {this.state.pokemon.map((pokemon) => {
                        return (
                            <Pokecard key={pokemon.name} 
                                name={pokemon.name} 
                                url={pokemon.url}
                            />);
                    })}
                {this.state.prev ?  <button onClick={this.prevPage}>Prev</button> : <div></div>}
                {this.state.next ?  <button onClick={this.nextPage}>Next</button> : <div></div>}
            </div>
            : (<div>Loading Pokemon</div>))
        );
    }
}

ReactDOM.render(
    <Pokedex name="Khanh's Pokedex"/>,
    document.getElementById('root')
);
