import React from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css'


class Pokedex extends React.Component{
    render(){
        return (
            <div>
                <h1>{this.props.name}</h1>
                <Dashboard/>
            </div>
        );
    }
}

class Pokecard extends React.Component{
    state = {
        name: '',
        imageUrl: '',
        pokemonIndex: ''
    };

    async componentDidMount(){
        const { name, url } = this.props;
        const urlSplit = url.split('/');
        const pokemonIndex = urlSplit[urlSplit.length - 2]
        const imageUrl = "https://pokeres.bastionbot.org/images/pokemon/" + pokemonIndex + ".png"
        this.setState({name: name, imageUrl: imageUrl, pokemonIndex: pokemonIndex})
    };

    render(){
        return (
            <div className='col-md-3 col-sm-6 md-5'>
                <h1>{this.state.name}</h1>
                <img src={this.state.imageUrl} alt= {this.state.name} width="100em" height="100em"></img>
            </div>
        );
    }
}

class Pokelist extends React.Component{
    state = {
        url: "https://pokeapi.co/api/v2/pokemon/",
        pokemon: null
    };

    async componentDidMount(){
        const res = await axios.get(this.state.url);
        this.setState({pokemon: res.data["results"]});
    }

    render(){
        return (
            (this.state.pokemon ? 
                (<React.Fragment>
                    {this.state.pokemon.map((pokemon) => {
                        return (
                            <Pokecard key={pokemon.name} 
                                name={pokemon.name} 
                                url={pokemon.url}
                            />);
                    })}
                </React.Fragment>)
            : (<div>Loading Pokemon</div>)));
    }
}

class Dashboard extends React.Component{
    render(){
        return (
            <div className='row'>
                <div className='col'>
                    <Pokelist />
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <Pokedex name="Khanh's Pokedex"/>,
    document.getElementById('root')
);
